(7, 'fr', '{\n  \"semantics\": [\n    {\n      \"label\": \"Image ou vidéo\",\n      \"fields\": [\n        {\n          \"label\": \"Type de média\",\n          \"description\": \"Média à afficher au-dessus de la question (facultatif).\"\n        },\n        {\n          \"label\": \"Désactiver le zoom sur image pour l\'image de la question\"\n        }\n      ]\n    },\n    {\n      \"label\": \"Question\"\n    },\n    {\n      \"label\": \"Options disponibles\",\n      \"entity\": \"option\",\n      \"field\": {\n        \"label\": \"Option\",\n        \"fields\": [\n          {\n            \"label\": \"Question\"\n          },\n          {\n            \"label\": \"Réponse correcte\"\n          },\n          {\n            \"label\": \"Aide et feedback\",\n            \"fields\": [\n              {\n                \"label\": \"Astuces\",\n                \"description\": \"Indication pour l\'utilisateur. Ce texte s\'affiche avant que l\'utilisateur ne valide la/les réponse(s).\"\n              },\n              {\n                \"label\": \"Commentaire (si cette réponse a été sélectionnée)\",\n                \"description\": \"Cette indication s\'affiche sous la réponse quand l\'utilisateur clique sur Vérifier.\"\n              },\n              {\n                \"label\": \"Commentaire (si cette réponse n\'a pas été sélectionnée)\",\n                \"description\": \"Après vérification par l\'utilisateur, s\'affiche sous la réponse si celle-ci n\'a pas été sélectionnée.\"\n              }\n            ]\n          }\n        ]\n      }\n    },\n    {\n      \"label\": \"Feedback général\",\n      \"fields\": [\n        {\n          \"widgets\": [\n            {\n              \"label\": \"Valeur par défaut\"\n            }\n          ],\n          \"label\": \"Ajouter des commentaires personnalisés selon le barème de votre choix\",\n          \"description\": \"Par exemple: 0-20% Peux faire mieux, 21-91% Dans la moyenne, 91-100% Bravo!\",\n          \"entity\": \"Tranche\",\n          \"field\": {\n            \"fields\": [\n              {\n                \"label\": \"Barème\"\n              },\n              {},\n              {\n                \"label\": \"Feedback selon le barème\",\n                \"placeholder\": \"Compléter\"\n              }\n            ]\n          }\n        }\n      ]\n    },\n    {\n      \"label\": \"Apparence\",\n      \"fields\": [\n        {\n          \"label\": \"Le texte qui apparaît sur votre bouton dans le champ Afficher la réponse\",\n          \"default\": \"Afficher la réponse\"\n        },\n        {\n          \"label\": \"Le texte qui apparaît sur votre bouton dans le champ Afficher la solution\",\n          \"default\": \"Afficher la solution\"\n        },\n        {\n          \"label\": \"Le texte qui apparaît sur votre bouton dans le champ Réessayer\",\n          \"default\": \"Recommencer\"\n        },\n        {\n          \"label\": \"Le texte qui apparaît sur votre bouton dans le champ Afficher les astuces\",\n          \"default\": \"Afficher les astuces\"\n        },\n        {\n          \"label\": \"Textual representation of the score bar for those using a readspeaker\",\n          \"description\": \"Available variables are :num and :total\",\n          \"default\": \"You got :num out of :total points\"\n        },\n        {\n          \"label\": \"Astuce disponible (ne s\'affiche pas)\",\n          \"default\": \"Astuce disponible\",\n          \"description\": \"Le texte de vocalisation du contenu utilisé par la synthèse vocale\"\n        },\n        {\n          \"label\": \"Feedback disponible (ne s\'affiche pas)\",\n          \"default\": \"Feedback disponible\",\n          \"description\": \"Le texte de vocalisation du contenu utilisé par la synthèse vocale\"\n        },\n        {\n          \"label\": \"Lire le commentaire (ne s\'affiche pas)\",\n          \"default\": \"Lire le commentaire\",\n          \"description\": \"Le texte de vocalisation du contenu utilisé par la synthèse vocale\"\n        },\n        {\n          \"label\": \"Mauvaise réponse (ne s\'affiche pas)\",\n          \"default\": \"Mauvaise réponse\",\n          \"description\": \"Le texte de vocalisation du contenu utilisé par la synthèse vocale\"\n        },\n        {\n          \"label\": \"Bonne réponse (ne s\'affiche pas)\",\n          \"default\": \"Bonne réponse\",\n          \"description\": \"Le texte de vocalisation du contenu utilisé par la synthèse vocale\"\n        },\n        {\n          \"label\": \"L\'option aurait dû être cochée\",\n          \"default\": \"Il fallait cocher ici\"\n        },\n        {\n          \"label\": \"Cette option n\'aurait pas dû être cochée\",\n          \"default\": \"Il ne fallait pas cocher ici !\"\n        },\n        {\n          \"label\": \"Le texte pour le message \\\"Réponse requise\\\" \",\n          \"default\": \"Répondez s\'il vous plait avant de visionner la solution.\"\n        }\n      ]\n    },\n    {\n      \"label\": \"Paramètres comportementaux\",\n      \"description\": \"Ces options vous permettent de gérer le comportement de l\'activité.\",\n      \"fields\": [\n        {\n          \"label\": \"Activer le bouton \\\"Recommencer\\\"\"\n        },\n        {\n          \"label\": \"Activer le bouton \\\"Afficher la Solution\\\"\"\n        },\n        {\n          \"label\": \"Activer le bouton \\\"Vérifier\\\"\"\n        },\n        {\n          \"label\": \"Type de Question\",\n          \"description\": \"Selectionner l\'apparence et le comportement de la question.\",\n          \"options\": [\n            {\n              \"label\": \"Automatique\"\n            },\n            {\n              \"label\": \"Choix multiples (Cases à cocher)\"\n            },\n            {\n              \"label\": \"Choix unique (Bouton radio)\"\n            }\n          ]\n        },\n        {\n          \"label\": \"Donner un point pour l\'activité complète\",\n          \"descript