(19, 'fi', '{\n  \"semantics\": [\n    {\n      \"label\": \"Painikkeen \\\"Tarkista\\\" nimi\",\n      \"default\": \"Tarkista\"\n    },\n    {\n      \"label\": \"Painikkeen \\\"Yritä uudelleen\\\" nimi\",\n      \"default\": \"Yritä uudelleen\"\n    },\n    {\n      \"label\": \"Tuloksen selite\",\n      \"default\": \"Saat oikeista vastauksista +1 pistettä. Vääristä vastauksista saat -1 pistettä. Pienin mahdollinen tulos on 0.\"\n    },\n    {\n      \"fields\": [\n        {\n          \"label\": \"Asetukset\",\n          \"fields\": [\n            {\n              \"label\": \"Taustakuva\",\n              \"description\": \"Valinnainen. Valitse taustakuva tehtävälle.\"\n            },\n            {\n              \"label\": \"Tehtäväalueen koko\",\n              \"description\": \"Määrittele pikseleissä tehtäväalueen koko.\"\n            }\n          ]\n        },\n        {\n          \"label\": \"Tehtävä\",\n          \"description\": \"Aloita asettamalla pudotuskohteet.<br/>Seuraavaksi, asettele pudotettavat elementit ja valitse niille halutut kohteet.<br/>Viimeiseksi, muokkaa uudestaan pudotuskohteita ja valitse niille oikeat raahattavat.\",\n          \"fields\": [\n            {\n              \"label\": \"Elementit\",\n              \"entity\": \"elementti\",\n              \"field\": {\n                \"label\": \"Elementti\",\n                \"fields\": [\n                  {\n                    \"description\": \"Valitse lisättävä sisältötyyppi.\"\n                  },\n                  {},\n                  {},\n                  {},\n                  {},\n                  {\n                    \"label\": \"Valitse pudotuskohteet\"\n                  },\n                  {\n                    \"label\": \"Taustan läpinäkyvyys 0-100 (0 = läpinäkyvä, 100 = valkoinen)\"\n                  },\n                  {\n                    \"label\": \"Loputon määrä elementin kopioita\",\n                    \"description\": \"Kloonaa elementin, jot